package de.dhbw;import java.util.ArrayList;import java.util.List;public class HappyLeapYearDetector {    public List<Integer> calculateHappyLeapYears(List<Integer> yearsToBeChecked) {        List<Integer> happyLeapYears = new ArrayList<>();        for (Integer year : yearsToBeChecked) {            if (isHappyLeapYear(String.valueOf(year)))            {                happyLeapYears.add(year);            }        }        return happyLeapYears;    }    public boolean isHappyLeapYear(String inputYear) {        Integer.valueOf(inputYear);       /* if (Integer.valueOf(inputYear) <= 0) {            return false;        }*/        if (Integer.valueOf(inputYear) % 100 == 0 && (Integer.valueOf(inputYear) % 400 != 0)) {            return false;        }        if (Integer.valueOf(inputYear) % 4 == 0) {            return true;        }        if (Integer.valueOf(inputYear) % 13 != 0) {            return true;        }        return false;    }}